function() {
  var ecommerceItems = {{DLV - ecommerce.items}}; // Assuming DLV - ecommerce.items is your data layer variable

  if (ecommerceItems && ecommerceItems.length > 0) {
    var criteoItemIds = [];

    for (var i = 0; i < Math.min(3, ecommerceItems.length); i++) {
      var item = ecommerceItems[i];
      var itemId = item.item_id;

      criteoItemIds.push(itemId);
    }

    return criteoItemIds;
  }

  return undefined; // Return undefined if there are no items in the data layer
}

function() {
  var clickText = {{Click Text}}; // Vervang door jouw variabele naam in GTM
  var pattern = /SHOP( >)?/; // Regex om te controleren op 'SHOP' of 'SHOP >'

  var splitText = clickText.split(pattern); // Split de tekst op basis van het patroon
  if (splitText.length > 0) {
    return splitText[0].trim().toLowerCase(); // Retourneert het eerste deel, getrimd en in lowercase
  }

  return ''; // Retourneert een lege string als er geen match is
}

