function() {
  var items = {{DLV - ecommerce.items}};
  var transformed = items.map(function(item) {
    return {
      id: item.item_id,
      name: item.item_name,
      brand: item.item_brand,
      price: parseFloat(item.price),
      quantity: parseInt(item.quantity),
      item_category: item.item_category,
      item_category2: item.item_category2,
      item_list_name: item.item_list_name
    };
  });
  return transformed;
}

function() {
  var ecomData = window.dataLayer || [];
  var allItemCategories = [];

  // Loop through the dataLayer to find the specific 'view_item' event
  for (var i = 0; i < ecomData.length; i++) {
    var event = ecomData[i];

    // Only process if the event is 'view_item'
    if (event.event === 'view_item' && event.ecommerce && event.ecommerce.items) {
      var items = event.ecommerce.items;

      // Loop through the items and collect 'item_category', 'item_category2', 'item_category3'
      items.forEach(function(item) {

        // Check and collect 'item_category'
        if (item.item_category && allItemCategories.indexOf(item.item_category) === -1) {
          allItemCategories.push(item.item_category);
        }

        // Check and collect 'item_category2'
        if (item.item_category2 && allItemCategories.indexOf(item.item_category2) === -1) {
          allItemCategories.push(item.item_category2);
        }

        // Check and collect 'item_category3'
        if (item.item_category3 && allItemCategories.indexOf(item.item_category3) === -1) {
          allItemCategories.push(item.item_category3);
        }
      });
    }
  }

  // Return the array directly (not as a JSON string)
  return allItemCategories;
}

